/* Lightweight marked replacement for GitHub Pages */
window.marked = {
  parse: function(s){
    s = String(s||"");
    return s
      .replace(/^### (.*)$/gm,'<h3>$1</h3>')
      .replace(/^## (.*)$/gm,'<h2>$1</h2>')
      .replace(/^# (.*)$/gm,'<h1>$1</h1>')
      .replace(/^\> (.*)$/gm,'<blockquote>$1</blockquote>')
      .replace(/\*\*(.+?)\*\*/g,'<strong>$1</strong>')
      .replace(/\*(.+?)\*/g,'<em>$1</em>')
      .replace(/`([^`]+)`/g,'<code>$1</code>')
      .replace(/\n{2,}/g,'</p><p>')
      .replace(/^(?!<h\d|<blockquote|<p|<ul|<ol|<li)(.+)$/gm,'<p>$1</p>');
  }
};
